(function(){tinymce.create('tinymce.plugins.PlaceHolderPlugin',{init:function(ed,url){ed.addCommand('mcePlaceHolder',function(){ed.windowManager.open({file:url+'/placeholder.htm',width:420+parseInt(ed.getLang('placeholder.delta_width',0)),height:160+parseInt(ed.getLang('placeholder.delta_height',0)),inline:1},{plugin_url:url});});ed.addButton('placeholder',{title:'Placeholder',image:url+'/img/placeholder.gif',cmd:'mcePlaceHolder'});},getInfo:function(){return{longname:'PlaceHolder',author:'Moxiecode Systems AB',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/advimage',version:tinymce.majorVersion+"."+tinymce.minorVersion};}});tinymce.PluginManager.add('placeholder',tinymce.plugins.PlaceHolderPlugin);})();